import gql from 'graphql-tag/lib/graphql-tag.umd';

const SINGLE_PRODUCT_ID = gql`
  query SINGLE_PRODUCT_ID($id: ID!) {
    Product(where: { id: $id }) {
      name
      price
      description
      id
      photo {
        altText
        image {
          publicUrlTransformed
        }
      }
    }
  }
`;

const UpdateProduct = ({ id }) => <p>Update! {id}</p>;
export default UpdateProduct;
