export const formatMoney = (amount = 0) => {
  const options = {
    style: 'currency',
    currency: 'TRY',
    minimumFractionDigits: 2,
  };

  const formatter = Intl.NumberFormat('tr-TR', options);
  return formatter.format(amount / 100);
};
