import { useState } from 'react';
import { useForm } from '../lib/useForm';

const CreatePruduct = () => {
  const { inputs, handleChange, clearForm } = useForm({
    name: 'Nice Shoes',
    price: 34552,
    description: 'These are best shoes!',
  });
  return (
    <form>
      <label htmlFor="name">
        Name:
        <input
          type="text"
          id="name"
          name="name"
          placeholder="name"
          onChange={handleChange}
          value={inputs?.name}
        />
      </label>
      <label htmlFor="price">
        Price:
        <input
          type="number"
          id="price"
          name="price"
          placeholder="price"
          onChange={handleChange}
          value={inputs?.price}
        />
      </label>
      <button type="button" onClick={clearForm}>
        Clear Form
      </button>
      <button type="button" onClick={clearForm}>
        Clear Form
      </button>
    </form>
  );
};

export default CreatePruduct;
