export const formatMoney = (amount = 0) => {
  const options = {
    style: 'currency',
    currency: 'TRY',
    minimumFractionDigits: 2,
  };

  const formmatter = Intl.NumberFormat('TRY', options);
  return formmatter.format(amount);
};
