import { useForm } from '../lib/useForm';
import Form from './styles/Form';

const CreatePruduct = () => {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    name: 'Nice Shoes',
    price: 34552,
    description: 'These are best shoes!',
  });
  return (
    <Form>
      <fieldset>
        <label htmlFor="image">
          Name:
          <input
            type="file"
            id="image"
            name="image"
            placeholder="image"
            onChange={handleChange}
            value={inputs?.image}
          />
        </label>
        <label htmlFor="name">
          Name:
          <input
            type="text"
            id="name"
            name="name"
            placeholder="name"
            onChange={handleChange}
            value={inputs?.name}
          />
        </label>
        <label htmlFor="price">
          Price:
          <input
            type="number"
            id="price"
            name="price"
            placeholder="price"
            onChange={handleChange}
            value={inputs?.price}
          />
        </label>
        <button type="submit">+ Add Product</button>
      </fieldset>
    </Form>
  );
};

export default CreatePruduct;
