import gql from 'graphql-tag/lib/graphql-tag.umd';

const SINGLE_ITEM_QUERY = gql`
  query SINGLE_ITEM_QUERY {
    Product(where: { id: "6060957bbd146253ee0fcb38" }) {
      name
      price
      description
    }
  }
`;

const SingleProduct = ({ query }) => (
  <div>hey iam single product page {query?.id}</div>
);

export default SingleProduct;
