import gql from 'graphql-tag/lib/graphql-tag.umd';
import { useQuery } from '@apollo/client';

const SINGLE_PRODUCT_ID = gql`
  query SINGLE_PRODUCT_ID($id: ID!) {
    Product(where: { id: $id }) {
      id
      name
      description
      price
    }
  }
`;

const UpdateProduct = ({ id }) => {
  const { loading, error, data } = useQuery(SINGLE_PRODUCT_ID);
  return <p>Update! {id}</p>;
};
export default UpdateProduct;
